import {React, useState} from 'react'
import Image from 'react-bootstrap/Image'
import Form from 'react-bootstrap/Form'
import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button'

import { ToastContainer, toast } from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css'


const CardDetailItem = ({show, close, character}) => {

  console.log(character)

  const [locationName, setLocationName] = useState()

  const handleLocationChange = (e) => {
    setLocationName(e.target.value)
  }

  const notify = (msg) => toast.error(msg, {
    position: "top-center",
    autoClose: 2000,
    hideProgressBar: false,
    theme: "colored",
    });

   const addLocation = () => {
    const locations = JSON.parse(localStorage.getItem('Location')) || []
    
    const charExist = locations.find(loc => {
        return Object.values(loc).flat().includes(character.name);
    })
    
    if (charExist) {
        notify('Character already exist in this location or another location')
        return
    }

    const checkLocationExist = locations.find(loc => loc.hasOwnProperty(locationName))

    if (checkLocationExist) { 
        const locationIndex = locations.findIndex(loc => loc.hasOwnProperty(locationName))
        const existingCharacters = locations[locationIndex][locationName]

        locations[locationIndex][locationName] = [...existingCharacters, character.name]
    } else {
        locations.push({ [locationName]: [character.name] })
    }

    localStorage.setItem('Location', JSON.stringify(locations))
    close()
  }

  return (
    <Modal show={show} onHide={close}>
        <Modal.Header closeButton>
          <Modal.Title>Detail Character</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Image src={character.image} fluid />
            <h4 className='mt-2'>{character.name}</h4>
            <p><b>Status :</b> Alive</p>
            <p style={{ lineHeight: '0' }}><b>Species :</b> Human</p>
            <p><b>Location :</b> {character.location.name}</p>
            <Form.Control 
              type="text" 
              placeholder="Normal text" 
              className='mt-3'
              value={locationName}
              onChange={handleLocationChange}
            />
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={close}>
            Close
          </Button>
          <Button variant="primary" onClick={() => {
                if (locationName) addLocation()
                else notify('Please enter the location name first')
            }}>
            Add Location
          </Button>
        </Modal.Footer>
        <ToastContainer/>
      </Modal>
  )
}

export default CardDetailItem
