import {React, useState} from 'react'
import Pic from '../assets/Tiger.jpg'
import Image from 'react-bootstrap/Image'
import Form from 'react-bootstrap/Form'
import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button'

import { ToastContainer, toast } from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css'


const CardDetailItem = ({show, close, data}) => {

  const [location, setLocation] = useState()

  const handleLocationChange = (e) => {
    setLocation(e.target.value)
  }

  const notify = () => toast("Wow so easy!")

  const addLocation = () => {
    const existingLocation = JSON.parse(localStorage.getItem(location)) || []


    
    for (const location in existingLocation) {
        if (existingLocation[location] === data.name) {
            notify()
            return
        }
    }

    

    // existingLocation.push(data.name)
    // localStorage.setItem(location, JSON.stringify(existingLocation))
    // close()
  }

  return (
    <Modal show={show} onHide={close}>
        <Modal.Header closeButton>
          <Modal.Title>Detail Character</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Image src={Pic} fluid />
            <h4 className='mt-2'>{data.name}</h4>
            <p><b>Status :</b> Alive</p>
            <p style={{ lineHeight: '0' }}><b>Species :</b> Human</p>
            <p><b>Location :</b> Earth (Replacement Dimension)</p>
            <Form.Control 
              type="text" 
              placeholder="Normal text" 
              className='mt-3'
              value={location}
              onChange={handleLocationChange}
            />
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={close}>
            Close
          </Button>
          <Button variant="primary" onClick={addLocation}>
            Add Location
          </Button>
        </Modal.Footer>
        <ToastContainer
            position="top-center"
            autoClose={2000}
            hideProgressBar={false}
            newestOnTop={false}
            rtl={false}
            pauseOnFocusLoss
            draggable
            theme="colored"
            pauseOnHover
            />
      </Modal>
  )
}

export default CardDetailItem
