import {React, useState} from 'react'
import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card'
import CardDetailItem from './CardDetailItem'

const CardItem = ({ character }) => {
    const [showDetail, setShowDetail] = useState(false)
    const [locationName, setLocationName] = useState('')

    console.log(character)

    const handleClose = () => setShowDetail(false)
    const handleOpen = () => {
      setLocationName('')
      setShowDetail(true)
    }

    const handleLocationChange = (e) => {
      setLocationName(e.target.value)
    }

  return (
    <>
    <Card style={{ width: '10rem' }}>
      <Card.Img variant="top" src={character.image}/>
      <Card.Body>
        <Card.Title>{character.name}</Card.Title>
        <Card.Text>
          <span>Species : {character.species}</span>
          <p>Status : {character.status}</p>
        </Card.Text>
        <Button variant="outline-warning" onClick={handleOpen}>Detail</Button>
      </Card.Body>
    </Card>
    {
     <CardDetailItem show={showDetail} close={handleClose} character={character} locationName={locationName} handleLocationChange={handleLocationChange}/>
    }
    </>
  )
}

export default CardItem
