import { React, useState } from 'react'
import CardItem from './CardItem'

const CharacterLocation = ({ data }) => {

  const characterList = data?.characters?.results

  const [showList, setShowList] = useState(false)
  const locationChar = JSON.parse(localStorage.getItem('Location')) || []

  if (locationChar && characterList) {
    return (
      <div className='text-center'>
        {
          locationChar.map((loc, index) => {
            const location = Object.keys(loc)[0]
            const characterAssign = loc[location].map((id) => {
                return characterList.filter((char) => {
                  if (char.id === id.toString()) {
                    return char
                  }
                })
            })

            console.log(characterAssign)

            return (
              <div>
                <h4 className='mt-4' key={index} onClick={() => setShowList(true)}>{location}</h4>
                {characterAssign && showList &&
                  <CardItem character={characterAssign}/>
                }
              </div>
            )
          })
        }
      </div>
    )
  }

  return (
    <div className='text-center p-5'>
      No Location Added Yet
    </div>
  )
}

export default CharacterLocation
