import './../node_modules/bootstrap/dist/css/bootstrap.min.css'
import CharacterLocation from './components/CharacterLocation'
import Header from './components/Header'
import Home from './components/Home'
import {Routes, Route, HashRouter} from 'react-router-dom'

import { ApolloClient, InMemoryCache, ApolloProvider, gql, useQuery } from '@apollo/client'
import { useEffect, useContext } from 'react'

const apolloClient = new ApolloClient({
  uri: 'https://rickandmortyapi.com/graphql',
  cache: new InMemoryCache(),
});

const GET_CHAR_LIST = gql`
  query getCharacterLists {
    characters(page:1) {
      results{
        id
        name
        status
        location {
          id
        }
        image
      }
    }
  }
`

function App() {
  const client = apolloClient

  const { data, updateData } = useContext(DataContext)

  const { loading, error, data: fetchData } = useQuery(GET_CHAR_LIST)

  useEffect(() => {
    if (fetchData) updateData(fetchData)
  }, [fetchData])

  return (
    <ApolloProvider client={client}>
      <HashRouter>
        <Header/>
        <Routes>
            <Route path='/' element={<Home/>}/>
            <Route path='/character-location' element={<CharacterLocation/>}/>
        </Routes>
      </HashRouter>
    </ApolloProvider>

  )
}

export default App
