import './../node_modules/bootstrap/dist/css/bootstrap.min.css'
import CharacterLocation from './components/CharacterLocation'
import Header from './components/Header'
import Home from './components/Home'
import {Routes, Route, HashRouter} from 'react-router-dom'

import { ApolloClient, InMemoryCache, ApolloProvider, gql, useQuery } from '@apollo/client'

const apolloClient = new ApolloClient({
  uri: 'https://rickandmortyapi.com/graphql',
  cache: new InMemoryCache(),
});

const GET_CHAR_LIST = gql`
  query getCharacterLists {
    characters(page:1) {
      results{
        id
        name
        status
        location {
          id
        }
        image
      }
    }
  }
`

// const apolloClient = () => {
//   return new ApolloClient({
//     link: new HttpLink({
//       uri: 'https://rickandmortyapi.com/graphql',
//       cache: new InMemoryCache()
//     })
//   })
// }

function App() {
  const client = apolloClient

  // const resultData = CharacterListQuery()

  return (
    <ApolloProvider client={client}>
      <HashRouter>
        <Header/>
        <Routes>
            <Route path='/' element={<Home/>}/>
            <Route path='/character-location' element={<CharacterLocation/>}/>
        </Routes>
      </HashRouter>
    </ApolloProvider>

  )
}

const CharacterListQuery = () => {
  const { loading, error, data } = useQuery(GET_CHAR_LIST)

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error : {error.message}</p>

  return data
}

export default App
