import {React, useState} from 'react'
import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card'
import Pic from '../assets/Tiger.jpg'
import CardDetailItem from './CardDetailItem'

const CardItem = (data) => {

    const [showDetail, setShowDetail] = useState(false)

    const handleClose = () => setShowDetail(false)
    const handleOpen = () => setShowDetail(true)

    const dummy = data.data

  return (
    <>
    <Card style={{ width: '10rem' }}>
      <Card.Img variant="top" src={Pic}/>
      <Card.Body>
        <Card.Title>{dummy.name}</Card.Title>
        <Card.Text>
          <span>Species : {dummy.species}</span>
          <p>Status : {dummy.status}</p>
        </Card.Text>
        <Button variant="outline-warning" onClick={handleOpen}>Detail</Button>
      </Card.Body>
    </Card>
    {
     <CardDetailItem show={showDetail} close={handleClose} data={dummy}/>
    }
    </>
  )
}

export default CardItem
